random_num = 0
def quote(request):
    template_name = "quotes/quote.html"
    this_time = random.randint(0,2)
    global random_num
    random_num=this_time

    #create a dictionary
    context = {
        'current_time': time.ctime(),
        'letter1' : quotes[this_time],
        "image": images[this_time]
    }
    return render(request, template_name, context)
    
