def about(request):
    template_name = "quotes/about.html"

    #create a dictionary
    context = {
        'image1': images[random_num],
        
        'info1': info[random_num],
    }

    return render(request, template_name, context)

def show_all(request):
    template_name = "quotes/show_all.html"

    #create a dictionary
    zipper = zip(quotes, images)
    context = {
        'all': zipper,
    }

    return render(request, template_name, context)
